import React, { useState, useEffect } from 'react';

export const UltimaPublicacion = () => {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Realizar la petición POST para obtener el nombre de usuario
    const fetchUsername = async () => {
      try {
        const response = await fetch('/api/username', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ /* datos a enviar en el POST */ }),
        });

        if (response.ok) {
          const data = await response.json();
          setUsername(data.username);
        } else {
          console.error('Error al obtener el nombre de usuario');
        }
      } catch (error) {
        console.error('Error al realizar la petición:', error);
      }
    };

    fetchUsername();
  }, []);

  return (
    <div>
      <div className="card rounded-3 p-4">
        <div className="text-center">
          <h2 className="fw-semibold">Últimas publicaciones</h2>
          {username && <p className="mt-2">Publicado por: {username}</p>}
        </div>
      </div>

      <br />
      <div className="card rounded-3 p-4">
        <div className="text-center">
          <h2 className="fw-semibold">Contenido</h2>
        </div>
      </div>
    </div>
  );
};
