import React, { useState } from 'react';


export const PhotoUploadForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [caption, setCaption] = useState('');

  const [image, setImage] = useState(null);
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('username', username);
      formData.append('password', password);
      formData.append('image_path', image);
      formData.append('caption', caption);


      const response = await fetch('http://127.0.0.1:5000/upload_photo', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Media ID:', data.media_id);
      } else {
        const error = await response.json();
        console.error('Error:', error.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };
  return (

<div>
<div className="text-center">
                <h2 className="fw-semibold">Última publicación</h2>
              </div>
    <form onSubmit={handleSubmit}>
      <div className="row mb-3">
        <div className="col">
          <label htmlFor="username" className="form-label">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="form-control"
          />
        </div>
        <div className="col">
          <label htmlFor="password" className="form-label">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-control"
          />
        </div>
      </div>
      <div className="row mb-3">
        <div className="col">
          <label htmlFor="image" className="form-label">Image:</label>
          <input
            type="file"
            id="image"
            onChange={handleImageChange}
            className="form-control"
          />
        </div>
      </div>
      <div className="row mb-3">
        <div className="col">
          <label htmlFor="caption" className="form-label">Caption:</label>
          <textarea
            type="text"
            id="caption"
            value={caption}
            onChange={(e) => setCaption(e.target.value)}
            className="form-control"
          />
        </div>

      </div>


      <div className="text-center">
        <button type="submit" className="btn btn-primary">Upload Photo</button>
      </div>
    </form>
    </div>

  );
}
