import React, { useState } from 'react';

 export const PhotoUploadForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [imagePath, setImagePath] = useState('');
  const [caption, setCaption] = useState('');
  const [altText, setAltText] = useState('');
  const [disableLikesAndViews, setDisableLikesAndViews] = useState(false);
  const [disableComments, setDisableComments] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('username', username);
      formData.append('password', password);
      formData.append('image_path', imagePath);
      formData.append('caption', caption);
      formData.append('alt_text', altText);
      formData.append('disable_likes_and_views', disableLikesAndViews ? '1' : '0');
      formData.append('disable_comments', disableComments ? '1' : '0');

      const response = await fetch('/upload_photo', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Media ID:', data.media_id);
      } else {
        const error = await response.json();
        console.error('Error:', error.error);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="imagePath">Image Path:</label>
        <input
          type="text"
          id="imagePath"
          value={imagePath}
          onChange={(e) => setImagePath(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="caption">Caption:</label>
        <input
          type="text"
          id="caption"
          value={caption}
          onChange={(e) => setCaption(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="altText">Alt Text:</label>
        <input
          type="text"
          id="altText"
          value={altText}
          onChange={(e) => setAltText(e.target.value)}
        />
      </div>
      <div>
        <label>
          <input
            type="checkbox"
            checked={disableLikesAndViews}
            onChange={(e) => setDisableLikesAndViews(e.target.checked)}
          />
          Disable Likes and Views
        </label>
      </div>
      <div>
        <label>
          <input
            type="checkbox"
            checked={disableComments}
            onChange={(e) => setDisableComments(e.target.checked)}
          />
          Disable Comments
        </label>
      </div>
      <button type="submit">Upload Photo</button>
    </form>
  );
};
