import React, { useState } from 'react';
import { PhotoUploadForm } from './instagram/Upload';

function App() {
  const [activeComponent, setActiveComponent] = useState('upload');

  const handleMenuItemClick = (componentName) => {
    setActiveComponent(componentName);
  };

  return (
    <div className="App">
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-9ndCyUaIbzAi2FUVXJi0CjmCapSmO7SnpJef0486qhLnuZ2cdeRhO02iuK6FUUVM" crossOrigin="anonymous"></link>

      <div className="container-fluid d-flex">
        {/* Menú a la izquierda */}
        <div className="col-3 bg-light border-end py-4">
          <ul className="list-group list-group-flush">
            <li className={`list-group-item ${activeComponent === 'upload' ? 'active' : ''}`}>
              <button className="btn btn-link text-decoration-none" onClick={() => handleMenuItemClick('upload')}>Upload</button>
            </li>
            {/* Agrega más opciones de menú aquí */}
          </ul>
        </div>

        {/* Contenido principal */}
        <div className="col-9 my-5">
          <div className="rounded-pill bg-primary text-white py-2 px-4 d-flex justify-content-center mb-4">
            <h1 className="m-0">Gestor de publicaciones</h1>
          </div>
          <div className="card rounded-3 p-4">
            {activeComponent === 'upload' && <PhotoUploadForm />}
            {/* Agrega más componentes aquí */}
          </div>
        </div>
        <footer className="text-center mt-4 col-12">
          &copy; 2024 - Photo Upload Tool
        </footer>
      </div>
    </div>
  );
}

export default App;
