import React, { useState, useEffect } from 'react';

export const UltimaPublicacion = () => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [caption, setCaption] = useState('');

    const [image, setImage] = useState(null);
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const formData = new FormData();
            formData.append('username', username);
            formData.append('password', password);
            formData.append('image_path', image);
            formData.append('caption', caption);


            const response = await fetch('http://127.0.0.1:5000/upload_photo', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Media ID:', data.media_id);
            } else {
                const error = await response.json();
                console.error('Error:', error.error);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <div className="card rounded-3 p-4">
                <div className="text-center">
                    <h2 className="fw-semibold">Ãšltimas publicaciones</h2>
                    {username && <p className="mt-2">Publicado por: {username}</p>}
                </div>
            </div>

            <br />
            <div className="card rounded-3 p-4">
                <div className="text-center">
                    <h2 className="fw-semibold">Contenido</h2>
                </div>
            </div>
        </div>
    );
};
